css:
.badge {
  border: 1px solid #eeeeee;
}
.state-arrow {
  margin: 185px 0 0 20px;
}
.state-arrow img {
  margin-bottom: 7px;
}
.state-arrow span {
  font-size: 24px;
  color: #a09f9e;
  font-weight: 200;
  margin-left: 15px;
}
.panel-title i.fa {
  margin-right: 6px;
}
.vgroup {
  flex-direction: column;
  display: flex;
}
.hgroup {
  display: flex;
  flex-direction: row;
}
/*
GLOBAL FLEXBOX LAYOUT STYLES
*/
.tab-views {
  flex-grow: 1;
  flex-shrink: 1;
  flex-basis: auto;
  display: flex;
  overflow: auto;
}
.tab {
  display: flex;
  width: 100%;
}
.rtss-databases,
.rt-perf,
.collections-table,
.schema-container,
.compass-documents,
.index-container,
.compass-explain,
.validation-container {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  width: 100%;
  flex-grow: 1;
  overflow: auto;
}
.controls-container {
  flex-grow: 0;
  flex-shrink: 0;
  flex-basis: auto;
  box-shadow: 0 2px 2px rgba(0, 0, 0, 0.2);
  z-index: 4;
  border-top: 1px solid #ebebed;
}
.column-container {
  height: 100%;
  flex-grow: 1;
  flex-shrink: 1;
  flex-basis: auto;
  display: flex;
  overflow-y: scroll;
  background: #f5f6f7;
}
.column.main {
  height: 100%;
  flex: 1;
  margin-bottom: 100px;
  padding: 0 15px;
}
.rtss,
.collections,
.collection-view {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  height: 100%;
}
::-webkit-scrollbar {
  width: 10px;
}
::-webkit-scrollbar:horizontal {
  height: 10px;
}
::-webkit-scrollbar-track {
  background-color: none;
}
::-webkit-scrollbar-track:hover {
  background-color: rgba(0, 0, 0, 0.16);
}
*::-webkit-scrollbar-thumb {
  background-color: rgba(0, 0, 0, 0.1);
  -webkit-border-radius: 10px;
  border-radius: 10px;
}
.unsafe-component {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  width: 100%;
  flex-grow: 1;
  overflow: auto;
}
.unsafe-component-has-error {
  color: red;
  padding: 10px;
  background-color: #ffcccc;
  display: flex;
  flex-direction: column;
}
.unsafe-component-message i {
  margin-right: 5px;
  font-size: 20px;
}
.unsafe-component-stack {
  padding-left: 25px;
  white-space: pre-wrap;
}
